package com.microservice.iam.infrastructure.persistence.jpa.repositories;

import com.microservice.iam.domain.model.aggregates.RefreshToken;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.stereotype.Repository;

import java.time.Instant;
import java.util.Optional;

/**
 * RefreshToken JPA Repository for AylluCare/B4U platform.
 * <p>
 *     Manages persistence of refresh tokens used in JWT authentication.
 * </p>
 */
@Repository
public interface RefreshTokenRepository extends JpaRepository<RefreshToken, Long> {

    /**
     * Find a refresh token by its token string.
     *
     * @param token the token string
     * @return an optional containing the RefreshToken if found
     */
    Optional<RefreshToken> findByToken(String token);

    /**
     * Find all refresh tokens for a specific user.
     *
     * @param userId the user ID
     * @return a list of RefreshToken entities
     */
    java.util.List<RefreshToken> findByUserId(Long userId);

    /**
     * Delete all expired tokens.
     *
     * @param now the current timestamp
     * @return the number of deleted tokens
     */
    int deleteByExpiresAtBefore(Instant now);

    /**
     * Delete all tokens for a specific user.
     *
     * @param userId the user ID
     */
    void deleteByUserId(Long userId);

    /**
     * Check if a token exists and is not revoked.
     *
     * @param token the token string
     * @return true if token exists and is not revoked
     */
    boolean existsByTokenAndRevokedFalse(String token);
}
